package com.rising.common.cache.model;

/**
 * 
 * <p>Title : CommonCache</p>
 * <p>Description : 普通缓存Model</p>
 * <p>Company : 杭州冉思科技有限公司</p>
 * @author : caoyang
 * @date : 2016年3月30日 上午10:20:31
 */
public class CommonCache {
	
	/**
	 * key的前缀
	 */
	private String keyPrefix;
	
	/**
	 * 缓存key
	 */
	private String key;
	
	/**
	 * 缓存过期时间，单位：s
	 */
	private Long expireSecond;
	
	/**
	 * 缓存值
	 */
	private String value;

	public CommonCache() {
		// TODO Auto-generated constructor stub
	}
	
	public CommonCache(String keyPrefix, String key, String value) {
		super();
		this.keyPrefix = keyPrefix;
		this.key = key;
		this.value = value;
	}

	public CommonCache(String keyPrefix, String key, Long expireSecond,
			String value) {
		super();
		this.keyPrefix = keyPrefix;
		this.key = key;
		this.expireSecond = expireSecond;
		this.value = value;
	}

	public String getKeyPrefix() {
		return keyPrefix;
	}

	public void setKeyPrefix(String keyPrefix) {
		this.keyPrefix = keyPrefix;
	}

	public String getKey() {
		return key;
	}

	public void setKey(String key) {
		this.key = key;
	}

	public Long getExpireSecond() {
		return expireSecond;
	}

	public void setExpireSecond(Long expireSecond) {
		this.expireSecond = expireSecond;
	}

	public String getValue() {
		return value;
	}

	public void setValue(String value) {
		this.value = value;
	}
	
	
	
}
